(window.webpackJsonp=window.webpackJsonp||[]).push([[36],{471:function(t,r,e){"use strict";e.r(r);var a=e(28),s=Object(a.a)({},(function(){var t=this,r=t._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[r("h1",{attrs:{id:"漢字"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#漢字"}},[t._v("#")]),t._v(" 漢字")]),t._v(" "),r("p",[t._v("原作者： Tae Kim\n")]),r("div",{staticClass:"video-container"},[r("iframe",{attrs:{width:"560",height:"315",src:"https://www.youtube.com/embed/_aorJUkr06c",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture",allowfullscreen:""}})]),r("p"),t._v(" "),r("h2",{attrs:{id:"什麼是漢字"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#什麼是漢字"}},[t._v("#")]),t._v(" 什麼是漢字？")]),t._v(" "),r("p",[t._v('日文裡面，名詞、形容詞詞幹和動詞基本都是用中文字寫的，稱爲「漢字」。副詞通常也是用漢字書寫，這意味著想要閱讀書面語，你必須學習中文字（幼兒讀物以及給那些不懂漢字的人看的材料除外）。但不是所有單字都是用漢字寫的，比如 "to do" 對應的動詞雖然應該有漢字對應，但事實上卻總是用平假名寫的。')]),t._v(" "),r("p",[t._v("本指南一開始就使用漢字，爲的是讓你盡快熟悉「真正的」日文。因此，我們先過一下漢字的基本特點，然後討論快速有需要學習的方法。掌握漢字不容易，但也不是不可能。學漢字最難的地方在於方法和時間，通過短期記憶學習漢字必須化很大的功夫，而且必須堅持下去。我的意思不是讓你每天學習五小時，而是每隔幾個月溫習一次，直到你確定記牢。這也是本指南一開始就引入漢字的另一個原因。在高級話題部分才介紹漢字是不行的。如果從一開始就隨着新詞彙學習漢字，這項巨大的工作就被分隔成小的、可控的單位，無形中也加深了對漢字的記憶。另外，這樣也有助於你學習新詞彙，因爲裡面往往就包含了你認識的漢字。如果你稍後才開始學漢字，這機會就白白被浪費掉了。")]),t._v(" "),r("h2",{attrs:{id:"學習漢字"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#學習漢字"}},[t._v("#")]),t._v(" 學習漢字")]),t._v(" "),r("p",[t._v("學習漢字所需要的所有資源都可以在網路上找到。你可以使用線上辭典，例如"),r("a",{attrs:{href:"https://www.edrdg.org/cgi-bin/wwwjdic/wwwjdic?1C",target:"_blank",rel:"noopener noreferrer"}},[t._v(" Jim Breen's WWWJDIC "),r("OutboundLink")],1),t._v("或者"),r("a",{attrs:{href:"https://jisho.org/",target:"_blank",rel:"noopener noreferrer"}},[t._v(" jisho.org"),r("OutboundLink")],1),t._v("。這兩個網站都不錯，並且有大部分漢字的筆順，對於初學者，我特別建議重複書寫以記憶筆順。寫漢字另一個重要的技巧是注意整體平衡感，不要寫的頭重腳輕。所以仿寫是很重要的。寫得多了你就會培養出一種筆順感覺，對於特定類型的文字會自然而然的習慣特定的寫法，不必再重複模仿。本指南中出現的所有漢字都可以很方便的複製貼上到線上辭典中學習。")]),t._v(" "),r("h2",{attrs:{id:"閱讀漢字"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#閱讀漢字"}},[t._v("#")]),t._v(" 閱讀漢字")]),t._v(" "),r("p",[t._v("基本每個漢字都有兩種讀法，稱作音読み （おんよみ）和訓読み（くんよみ）。音読み是原本的中文讀法，而訓読み是日文讀法。組合出現的漢字（熟語）通常用音読み發音，而單獨的漢字邊用訓読み發音。比如「力」（ちから）適用音読み發音，而一旦這個字放在組合裡面，例如「能力」，就要用音読み來發音了，（本例中是「りょく」）。")]),t._v(" "),r("p",[t._v("特定的漢字（特別是那些常見的）會有不止一種音読み或者訓読み。比如單字「怪力」裏面的「力」都做「りき」，而非「りょく」。特定的組合字也有特別的發音，跟拆開每隔漢字都不一樣，這些發音都要單獨記憶，幸好這種情況很少見。")]),t._v(" "),r("p",[t._v("除了單獨的文字以外，形容詞和動詞也會用訓読み發音，但後面邊都會跟着一些假名（稱爲「送假名」）。這種情況下，雖然單字有變化，但漢字的讀法是不變的。比如動詞「食べる」的過去形是「食べた」，其中漢字「食」的讀音沒有變化（試想如果不是這樣的話會有多難）。送假名也用於區分自動詞和他動詞（後面會說到）。")]),t._v(" "),r("p",[t._v("初學者還有一個比較難掌握的概念，那就是漢字在組合詞裡面也許會超微變化一下讀音，讓單字更好唸。比較常見的包括 / h / 音變爲 / b / 或者 / p / 音，或者「つ」變爲「っ」。例子包括「一本」、「徹底」、和「格好」。")]),t._v(" "),r("p",[t._v("漢字還有一個有趣的地方，有時候兩個單字讀法一樣，意思也差不多，但用的卻是不同的字，用以表示一些細微的不同含義。比如「聞く」（きく）和「聴く」（きく）都是「聽」的意思，區別在於「聴く」更偏重於表示聽什麼的東西這個動作，比如聽音樂一般就用「聴く」這個動詞，而不用「聞く」。「聞く」既可以表示「問」，也可以表示「聽」，但「訊く」（きく）就只能表示「问」。另一個常見的用法就是在看電影之類的演出時要用「観る」而不是「見る」。還有一個有意思的例子就是「書く」（かく）表示「寫」，而描く （かく）表示「畫」。但當你要表示描繪抽象物體比如書中的一個場景的時候，「描く」的讀法又要變成「えがく」。這種漢字含義一致但讀法不同的例子還有「今日」，它可以讀成「きょう」、「こんじつ」或者「こんにち」。這種情況下你用哪一種讀音其實區別不大，只是在特定場景可能要選擇某一種。")]),t._v(" "),r("p",[t._v("最後介紹一個特殊的算不上字的字：々，它用來表示重複前面的漢字，例如「時時」、「様様」、「色色」、「一一」也可以写成「時々」、「様々」、「色々」、「一々」。")]),t._v(" "),r("p",[t._v("除了這些「特點」之外，隨著學習的深入，你會發現漢字越來越多神奇的地方（你可以把這句話看成是諷刺）。不過，不要被嚇到認爲日文非常難學。日文裡面很多單字都只對應一個漢字，而且大多數漢字都只有一種讀音。")]),t._v(" "),r("h2",{attrs:{id:"爲何要用漢字"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#爲何要用漢字"}},[t._v("#")]),t._v(" 爲何要用漢字？")]),t._v(" "),r("p",[t._v("有些人認為使用完全不同的幾種文字而非一種統一的字母表實在是太複雜了。日文和中文本來就風馬牛不相及，所以也許使用漢字並不是個好主意。但本指南不想討論為何日文改變成什麽樣，而是要告訴你為何學好日文必須學習漢字。所以對於這個問題，我的意思是「事實如此，你改變不了什麽！」")]),t._v(" "),r("p",[t._v('你也許想知道為何不是用羅馬字母替代漢字以避免如此大的記憶量。實際上，韓國人使用自己發明的字母表用於書寫，也取得了巨大的成功。所以日本人為何不如此？我想任何一個學過一陣子日文的人都能回答這個問題。當你想把平假名改寫為漢字時，選擇一般總是不止一個（同音），有時候甚至能有十個重音（你可以試試看 "kikan"）。由於發音數量有限，導致日文的重音是必然的。相比之下，韓文里面有 14 個輔音和 10 個元音，這樣就有了 140 種發音組合。另外，一個字母甚至可以疊加第三個、第四個輔音，理論上韓文里面可以搭配出超過 1960 種發音（實際使用的發音遠少於此，但還是比日文多得多）。')]),t._v(" "),r("p",[t._v('因為人們總是希望讀得比說得快，所以文字最好能讓人迅速辨認出單字的含義。比如英文里面你可以通過單字的「形狀」來快速區分出不同的單字，試試看 "Hi, enve thgouh all teh wrods aer seplled icorrenctly, can you sltil udsternand me?"（編者註：中文也是一樣的，研表究明漢字的序順並不定一能影閱響讀）。韓文也可以做到，因為文字的變化足夠多，相互之間可以區分開。但是由於特徵還是不如漢字明顯，所以必須加入空格（這就出現另外一個問題：什麽時候該在什麽地方加空格）。')]),t._v(" "),r("p",[t._v("使用漢字後，我們就無需考慮加空格的問題，同音字的問題也基本解決了。否則的話，即使加入空格，歧義以及不足的視覺區分度會使得日文文本更難閱讀。")])])}),[],!1,null,null,null);r.default=s.exports}}]);